if [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].key}' | grep -q type` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].operator}' | grep -q "In"` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].values}' | grep -q config` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].key}' | grep -q type` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].operator}' | grep -q "In"` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].values}' | grep -q data` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].enforcement}' | grep -q preferred` ]]
